import { ChangeEvent, useEffect, useState } from 'react'
import axios from 'axios'
import '../style/Card.css'

type Product = {
  id: string
  name: string
  color: string
  price: number
  stock: number
}

type CardProps = {
  itemTitle: string
  closeCard: Function
}

export const Card = ({itemTitle, closeCard}: CardProps) => {
  const [product, setProduct] = useState<Product>({id: 'apple', name: 'Apple', color: 'red', price: -1, stock: -1})
  const [quantity, setQuantity] = useState(0)

  /* useEffect(() => {
    axios.get(`/products/${itemTitle}`)
  .then((res) => {
    console.log(res)
    setProduct(res.data)
  })
  .catch(function (error) {
    // TODO: Need to catch it
    console.log(error);
  })
  }, []) */

  const handleCloseCard = () => {
    closeCard(itemTitle)
  }

  const handleQuantity = (value: string) => {
    setQuantity(parseInt(value))
  }

  return (
    <>

      <div className='card-container'>
        <div className="header">
          <div className="fruit-color"> {/* style={{'background-color': product.color}} */}
            {/* img */}
            <div>{product.name}</div>
          </div>
          <div className="cancel-fruit"
               onClick={handleCloseCard}>
            X
          </div>
        </div>
        <div className="quantity">
          <div>
            <label>Selected quantity:</label>
          </div>
          <input type="range"
                 min='0'
                 max='10'
                 onChange={(event) => handleQuantity(event.target.value)} />
        </div>
        <div className="price">
          Price: {quantity*product.price}
        </div>
      </div>
    </>
  );
}
