import React, { useState } from 'react';
import '../../style/Dropdown.css'
import { FaApple, FaCaretDown, FaCaretUp } from 'react-icons/fa';
import { DropdownItem } from './DropdownItem';

type DropdownProps = {
    selected: Function
}

type Item = {
  title: string,
  selected: boolean,
}

export const Dropdown = ({selected}: DropdownProps) => {
  const OPTIONS = [
    {
      title:'Apple',
      selected: false,
      /* imgUrl:'apple.svg' */
    }, 
    {
      title: 'Carrot',
      selected: false,
    }, 
    {
      title: 'Melone',
      selected: false,
    },
    {
      title: 'Pear',
      selected: false,
    }
    , 
    {
      title:'Lemon',
      selected: false,
    }, 
    {
      title:'Orange',
      selected: false,
    }, 
    {
      title: 'Salad', 
      selected: false,
    }
  ]
  const [showMenu, setShowMenu] = useState(false)
  const [selectedItems, setSelectedItems] = useState<Item[]>([])
  const handleClick = (item: Item) => {
    const currentOptionIndex = selectedItems.findIndex((selectedItem) => {
      return item.title === selectedItem.title
    })
    const tempArr = [...selectedItems]
    tempArr[currentOptionIndex].selected = item.selected
    console.log(tempArr)
    setSelectedItems([...tempArr])
    selected(selectedItems)
  }
  return (
    <>
      <div className='dropdown-container'>
        <div className='dropdown-header-container'
             onClick={() => {setShowMenu(!showMenu)}}>
          <div className='dropdown-header'>
            Select to add item to basket
          </div>
          <div className='caret'>
            {showMenu ? <FaCaretUp /> : <FaCaretDown />}
          </div>
        </div>
        {
          showMenu
          && 
          <div className="dropdown-content">
            {OPTIONS.map((option, idx) => {
                return <DropdownItem key={idx}
                                      option={option}
                                      handleClick={() => {handleClick}}/>
            })}
          </div>
        }
      </div>
    </>
  );
}
