import React, { useEffect, useState } from 'react';
import {Dropdown} from './components/Dropdown/Dropdown'
import './style/App.css'
import {Card} from './components/Card'
import {Summary} from './components/Summary'

type Item = {
  title: string,
  selected: boolean,
}

type Product = {
  id: string
  name?: string
  color?: string
  price?: number
  stock?: number
  totalPrice?: number
  userQuantity?: number
}

export const App = () => {
  const INITIAL_ITEMS = [
    {
      title:'Apple',
      selected: false,
    }, 
    {
      title: 'Carrot',
      selected: false,
    }, 
    {
      title: 'Melone',
      selected: false,
    },
    {
      title: 'Pear',
      selected: false,
    }
    , 
    {
      title:'Lemon',
      selected: false,
    }, 
    {
      title:'Orange',
      selected: false,
    }, 
    {
      title: 'Salad', 
      selected: false,
    }
  ] as Item[]
  const [products, setProducts] = useState<Product[]>([])
  const [items, setItems] = useState<Item[]>([...INITIAL_ITEMS])
  const [filteredSelectedItems, setFilteredSelectedItems] = useState<Item[]>([])
  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
  }

  useEffect(() => {
    setFilteredSelectedItems(items.filter((item:Item) => {
      return item.selected
    }))
  }, [items])

  const getSelected = ((items:Item[]) => {
    setItems(items)
    const filteredItems = items.filter((item:Item) => {
      return item.selected
    })
    const mapItems = filteredItems.map((item:Item) => {
      return {
        id: item.title.toLowerCase(),
        name: item.title
      }
    })
    setProducts([...mapItems])
  })

  const updateItems = ((item:string) => {
    const currentOptionIndex = items.findIndex((selectedItem) => {
      return item.toLowerCase() === selectedItem.title.toLowerCase()
    })
    const tempArr = [...items]
    tempArr[currentOptionIndex].selected = false
    setItems([...tempArr])
  })

  const updateProducts = (product:Product) => {
    const currentOptionIndex = products.findIndex((innerProduct) => {
      return product.id === innerProduct.id
    })
    const tempArr = [...products]
    tempArr[currentOptionIndex].stock = product.stock
    tempArr[currentOptionIndex].userQuantity = product.userQuantity
    tempArr[currentOptionIndex].totalPrice = product.totalPrice
    console.log(products)
    setProducts([...tempArr])
  }

  return (
    <div className='app'>
      <Dropdown selected={getSelected} items={items}/>
      <form onSubmit={(event:React.FormEvent<HTMLFormElement>) => handleSubmit(event)}>
        <div className='cards-container'>
          {filteredSelectedItems.map((selectedItem:Item) => {
            return <Card itemTitle={selectedItem.title.toLowerCase()} 
                         closeCard={updateItems}
                         updateProduct={updateProducts}
                         key={selectedItem.title}/>
          })}
        </div>
      </form>
      {products && <Summary products={products}/>}
    </div>
  );
}
