import { useEffect, useState } from 'react';
import '../../style/DropDownItem.css'

type Item = {
  title: string,
  selected: boolean,
}

type DropdownItemProps = {
    option: Item
    handleDropdownClick: Function
}

export const DropdownItem = ({option, handleDropdownClick}: DropdownItemProps) => {
  const [logo, setLogo] = useState('')
  const handleClick = (option: Item) => {
    option.selected ? option.selected = false : option.selected = true
    handleDropdownClick(option)
  }
  useEffect(() => {
    setLogo(require(`../../images/${option.title.toLowerCase()}.svg`).default)
  }, [option])
  return (
    <>
      <div className='item-conteiner'
           onClick={() => handleClick(option)}>
        <div className='item-option'>
        {option.title}
        </div>
        <div className='seprator'></div>
        {/* <img src={require("../../images/a.svg").default} alt="logo" /> */}
        <img src={logo} alt={`${option.title.toLowerCase()}`} />
        {console.log(logo)}
      </div>
    </>
  );
}
