import React, { useEffect, useState } from 'react';
import {Dropdown} from './components/Dropdown/Dropdown'
import {Card} from './components/Card'

type Item = {
  title: string,
  selected: boolean,
}

export const App = () => {
  const [items, setItems] = useState<Item[]>([
    {
      title:'Apple',
      selected: false,
      /* imgUrl:'apple.svg' */
    }, 
    {
      title: 'Carrot',
      selected: false,
    }, 
    {
      title: 'Melone',
      selected: false,
    },
    {
      title: 'Pear',
      selected: false,
    }
    , 
    {
      title:'Lemon',
      selected: false,
    }, 
    {
      title:'Orange',
      selected: false,
    }, 
    {
      title: 'Salad', 
      selected: false,
    }
  ])
  const [selectedItems, setSelectedItems] = useState<Item[]>([])
  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
  }

  useEffect(() => {
    setSelectedItems(items.filter((item: Item) => {
      return item.selected
    }))
  }, [items])

  const getSelected = ((items:Item[]) => {
    setSelectedItems(items)
  })
  return (
    <div className="App">
      <Dropdown selected={getSelected} items={items}/>
      <form onSubmit={(event:React.FormEvent<HTMLFormElement>) => handleSubmit(event)}>
        {selectedItems.length}
        {selectedItems.map((selectedItem:Item) => {
          return <Card itemTitle={selectedItem.title.toLowerCase()} />
        })}
      </form>
    </div>
  );
}
