import { useEffect, useState } from 'react'
import axios from 'axios'
import '../style/Card.css'

type Product = {
  id: string
  name: string
  color: string
  price: number
  stock: number
  totalPrice?: number
  userQuantity?: number
}

type CardProps = {
  itemTitle: string
  closeCard: Function
  updateProduct: Function
}

export const Card = ({itemTitle, closeCard, updateProduct}: CardProps) => {
  const mock = [
    {
      id: 'apple',
      name: 'Apple',
      color: 'red',
      price: 2,
      stock: 10,
    },
    {
      id: 'carrot',
      name: 'Carrot',
      color: 'orange',
      price: 4,
      stock: 30,
    },
    {
      id: 'melone',
      name: 'Melone',
      color: 'orange',
      price: 6,
      stock: 10,
    },
    {
      id: 'pear',
      name: 'Pear',
      color: 'orange',
      price: 2,
      stock: 1,
    },
    {
      id: 'lemon',
      name: 'Lemon',
      color: 'yellow',
      price: 6,
      stock: 90,
    },
    {
      id: 'orange',
      name: 'Orange',
      color: 'orange',
      price: 10,
      stock: 0,
    },
    {
      id: 'salad',
      name: 'Salad',
      color: 'green',
      price: 5,
      stock: 20,
    },
  ] as Product[]
  const [product, setProduct] = useState<Product>({id: 'apple', name: 'Apple', color: 'red', price:-1, stock:-1})
  const [quantity, setQuantity] = useState(0)
  const [icon, setIcon] = useState('')
/* 
  useEffect(() => {
    const findCorrectProductFromMock = mock.findIndex((mockProduct: Product) => {
      return mockProduct.id === itemTitle
    })
    setProduct(mock[findCorrectProductFromMock])
  }, []) */

  useEffect(() => {
    const totalPrice = quantity * product.price
    setProduct({...product,
      stock: 0,
    })
    updateProduct(product)
  }, [quantity])

  useEffect(() => {
    /* axios.get(`https://front-end-exam-dot-winky-apis.ew.r.appspot.com/products/${itemTitle}`)
    .then((res) => {
      console.log(res)
      setProduct(res.data)
    })
    .catch(function (error) {
      // TODO: Need to catch it
      console.log(error);
    }) */
    const findCorrectProductFromMock = mock.findIndex((mockProduct: Product) => {
      return mockProduct.id === itemTitle
    })
    if(!product) setProduct(mock[findCorrectProductFromMock])
  }, [itemTitle])

  useEffect(() => {
    const importedIcon = async () => {
      const iconPath = await import(`../images/${itemTitle.toLowerCase()}.svg`)
      setIcon(iconPath.default)
      return iconPath
    }
    importedIcon()
    const findCorrectProductFromMock = mock.findIndex((mockProduct: Product) => {
      console.log('itemTitle', itemTitle)
      console.log('mockProduct.id', mockProduct.id)
      return mockProduct.id === itemTitle
    })
    setProduct(mock[findCorrectProductFromMock])
  }, [itemTitle])

  const handleCloseCard = () => {
    closeCard(itemTitle)
  }

  const handleQuantity = (event:any) => {
    console.log('event', event)
    const value = event.target.value
    console.log('value', value)
    setQuantity(value)
  }

  return (
    <div className='card-container'>
      <div className="header">
        <div className="fruit"
             style={{backgroundColor: product.color}}>
          <img src={icon} 
               alt="product image"
               className='card-img'/>
          <label className="product-title">{product?.name}</label>
        </div>
        <div className="cancel-container"
             onClick={handleCloseCard}>
          <div className="cancel-fruit">
            x
          </div>
        </div>
      </div>
      <div className="quantity">
        <div className="quantity-header">
          <label className='quantity-label'>Selected quantity:</label>
          <div>
            <span className='quantity-number'>{product.userQuantity}</span> KG
          </div>
        </div>
        <input type="range"
               min='0'
               max={product.stock.toString()}
               onInput={(event) => handleQuantity(event)} />
      </div>
      <div className="price">
        Price: <span className='price-number'>${(product.userQuantity? product.userQuantity : 0) * product?.price}</span>
      </div>
    </div>
  );
}
