import { useEffect } from 'react';
import '../../style/DropDownItem.css'
import { FaCaretDown, FaCaretUp } from 'react-icons/fa';

type Item = {
  title: string,
  selected: boolean,
}

type DropdownItemProps = {
    option: Item
}

export const DropdownItem = ({option}: DropdownItemProps) => {
  const handleClick = () => {
    return option.selected ? option.selected = false : option.selected = true

  }
  return (
    <>
      <div className='item-conteiner'
           onClick={() => handleClick()}>
        <div className='item-option'>
        {option.title}
        </div>
        <div className='seprator'></div>

      </div>
    </>
  );
}
