import { useState } from 'react';
import '../../style/Dropdown.css'
import { FaCaretDown, FaCaretUp } from 'react-icons/fa';
import { DropdownItem } from './DropdownItem';

type Item = {
  title: string,
  selected: boolean,
}

type DropdownProps = {
    selected: Function
    items: Item[]
}

export const Dropdown = ({selected, items}: DropdownProps) => {
  const [showMenu, setShowMenu] = useState(false)
  const [selectedItems, setSelectedItems] = useState<Item[]>([])
  const handleClick = (item: Item) => {
    const currentOptionIndex = items.findIndex((selectedItem) => {
      return item.title.toLowerCase() === selectedItem.title.toLowerCase()
    })
    const tempArr = [...items]
    tempArr[currentOptionIndex].selected = item.selected
    setSelectedItems([...tempArr])
    selected(selectedItems)
  }
  return (
    <>
      <div className='dropdown-container'>
        <div className='dropdown-header-container'
             onClick={() => {setShowMenu(!showMenu)}}>
          <div className='dropdown-header'>
            Select to add item to basket
          </div>
          <div className='caret'>
            {showMenu ? <FaCaretUp /> : <FaCaretDown />}
          </div>
        </div>
        {
          showMenu
          && 
          <div className="dropdown-content">
            {items.map((option, idx) => {
                return <DropdownItem key={idx}
                                      option={option}
                                      handleDropdownClick={() => handleClick(option)}/>
            })}
          </div>
        }
      </div>
    </>
  );
}
