import { useEffect, useState } from 'react'
import axios from 'axios'
import '../style/Card.css'

type Product = {
  id: string
  name: string
  color: string
  price: number
  stock: number
}

type CardProps = {
  itemTitle: string
  closeCard: Function
}

export const Card = ({itemTitle, closeCard}: CardProps) => {
  const mock = [
    {
      id: 'apple',
      name: 'Apple',
      color: 'red',
      price: 2,
      stock: 10,
    },
    {
      id: 'carrot',
      name: 'Carrot',
      color: 'orange',
      price: 4,
      stock: 30,
    },
    {
      id: 'lemon',
      name: 'Lemon',
      color: 'yellow',
      price: 6,
      stock: 90,
    },
    {
      id: 'salad',
      name: 'Salad',
      color: 'green',
      price: 5,
      stock: 20,
    },
  ] as Product[]
  const [product, setProduct] = useState<Product>({id: '', name: '', color: '', price: -1, stock: -1})
  const [icon, setIcon] = useState('')
  const [quantity, setQuantity] = useState(0)

  /* useEffect(() => {
    axios.get(`/products/${itemTitle}`)
  .then((res) => {
    console.log(res)
    setProduct(res.data)
  })
  .catch(function (error) {
    // TODO: Need to catch it
    console.log(error);
  })
  }, []) */

  useEffect(() => {
    const importedIcon = async () => {
      const iconPath = await import(`../images/${itemTitle.toLowerCase()}.svg`)
      setIcon(iconPath.default)
      return iconPath
    }
    importedIcon()
  }, [itemTitle])

  const handleCloseCard = () => {
    closeCard(itemTitle)
  }

  const handleQuantity = (value: string) => {
    const totalPrice = quantity * product.price
    setQuantity(parseInt(value))
  }

  return (
    <>

      <div className='card-container'>
        <div className="header">
          <div className="fruit-color"> {/* style={{'background-color': product.color}} */}
            <img src={icon} alt="product img" />
            <div>{product.name}</div>
          </div>
          <div className="cancel-fruit"
               onClick={handleCloseCard}>
            X
          </div>
        </div>
        <div className="quantity">
          <div>
            <label>Selected quantity:</label>
            <div><span>{quantity}</span> KG</div>
          </div>
          <input type="range"
                 min='0'
                 max='10'
                 onChange={(event) => handleQuantity(event.target.value)} />
        </div>
        <div className="price">
          Price: {quantity*product.price}
        </div>
      </div>
    </>
  );
}
