import { useEffect, useState } from 'react'
import axios from 'axios';

type Product = {
  id: string
  name: string
  color: string
  price: number
  stock: number
}

type CardProps = {
  itemTitle: string
}

export const Card = ({itemTitle}: CardProps) => {
  const [itemColor, setItemColor] = useState('')
  useEffect(() => {
    axios.get(`/products/${itemTitle}`)
  .then((res) => {
    console.log(res);
    setItemColor(res.data.color)
  })
  .catch(function (error) {
    // TODO: Need to catch it
    console.log(error);
  })
  }, [])

  return (
    <>
      <div className='card-container'>
        <div className="heaser">
          <div className="fruit-color"></div>
          <div className="cancel-fruit"></div>
        </div>
        <div className="quantity"></div>
        <div className="price"></div>
      </div>
    </>
  );
}
