import React, { useState } from 'react';
import {Dropdown} from './components/Dropdown/Dropdown'
import { FaCarrot, FaLemon } from 'react-icons/fa';

export const App = () => {
  const OPTIONS = ['Apple', 'Carrot', 'Melone', 'Pear', 'Lemon', 'Orange', 'Salad']
  /* const ITEMICON = ['Apple', 'Carrot', 'Melone', 'Pear', 'Lemon', 'Orange', 'Salad'] */
  const [selectedItems, setSelectedItems] = useState<string[]>([])
  const getSelected = ((items:string[]) => {
    setSelectedItems(items)
  })
  return (
    <div className="App">
      <Dropdown options={OPTIONS} selected={() => {getSelected}}/>
    </div>
  );
}
