import React, { useState } from 'react';
import '../../style/Dropdown.css'
import { FaApple, FaCaretDown, FaCaretUp } from 'react-icons/fa';
import { DropdownItem } from './DropdownItem';

type DropdownProps = {
    options: string[]
    itemIcon: string[]
}

export const Dropdown = ({options, itemIcon}: DropdownProps) => {
  const [showMenu, setShowMenu] = useState(false)
  return (
    <>
      <div className='dropdown-container'>
        <div className='dropdown-header-container'
             onClick={() => {setShowMenu(!showMenu)}}>
          <div className='dropdown-header'>
            Select to add item to basket
          </div>
          <div className='caret'>
            {showMenu ? <FaCaretUp /> : <FaCaretDown />}
          </div>
        </div>
        {
          showMenu
          && 
          <div className="dropdown-content">
            {options.map((option) => {
                return <DropdownItem option={option} itemIcon={itemIcon}/>
            })}
          </div>
        }
      </div>
    </>
  );
}
