import React, { useEffect, useState } from 'react';
import {Dropdown} from './components/Dropdown/Dropdown'
import './style/App.css'
import {Card} from './components/Card'

type Item = {
  title: string,
  selected: boolean,
}

export const App = () => {
  const ITEMS = [
    {
      title:'Apple',
      selected: false,
    }, 
    {
      title: 'Carrot',
      selected: false,
    }, 
    {
      title: 'Melone',
      selected: false,
    },
    {
      title: 'Pear',
      selected: false,
    }
    , 
    {
      title:'Lemon',
      selected: false,
    }, 
    {
      title:'Orange',
      selected: false,
    }, 
    {
      title: 'Salad', 
      selected: false,
    }
  ] as Item[]
  const [selectedItems, setSelectedItems] = useState<Item[]>([...ITEMS])
  const [filteredSelectedItems, setFilteredSelectedItems] = useState<Item[]>([])
  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
  }

  useEffect(() => {
    setFilteredSelectedItems(ITEMS.filter((item:Item) => {
      return item.selected
    }))
  }, [selectedItems])

  const getSelected = ((items:Item[]) => {
    setSelectedItems(items)
  })
  return (
    <div className="App">
      <Dropdown selected={getSelected} items={ITEMS}/>
      <form onSubmit={(event:React.FormEvent<HTMLFormElement>) => handleSubmit(event)}>
        <div className='cards-container'>
          {filteredSelectedItems.map((selectedItem:Item) => {
            return <Card itemTitle={selectedItem.title.toLowerCase()} key={selectedItem.title}/>
          })}
          {filteredSelectedItems.length}
        </div>
      </form>
    </div>
  );
}
